import * as React from 'react';
import { cva, type VariantProps } from 'class-variance-authority';
import { cn } from '@/lib/utils';
import { X } from 'lucide-react';
import { Button } from './button';
const alertVariants = cva('relative w-full rounded-lg border px-4 py-3 text-sm grid has-[>svg]:grid-cols-[calc(var(--spacing)*4)_1fr] grid-cols-[0_1fr] has-[>svg]:gap-x-3 gap-y-0.5 items-start [&>svg]:size-4 [&>svg]:translate-y-0.5 [&>svg]:text-current transition-opacity duration-200', { variants: { variant: { default: 'bg-card text-card-foreground', destructive: 'text-destructive bg-destructive/10 [&>svg]:text-current *:data-[slot=alert-description]:text-destructive/90', success: 'text-foreground bg-green-900 [&>svg]:text-green-500 *:data-[slot=alert-description]:text-green-800/90', warning: 'text-foreground bg-yellow-900 [&>svg]:text-yellow-500 *:data-[slot=alert-description]:text-yellow-800/90', info: 'text-foreground bg-blue-900 [&>svg]:text-blue-500 *:data-[slot=alert-description]:text-blue-800/90' }, mode: { solid: 'border-transparent', outline: 'border-2 border-border bg-transparent', dashed: 'border-2 border-dashed border-border bg-transparent' } }, defaultVariants: { variant: 'default', mode: 'solid' } });
type AlertProps = React.ComponentProps<'div'> & VariantProps<typeof alertVariants> & { close?: boolean; onClose?: () => void };
export function Alert({ className, variant, children, close, onClose, mode, ...props }: AlertProps) {
return (<div data-slot='alert' role='alert' aria-live='polite' className={cn(alertVariants({ variant, mode }), className)} {...props}>
{children}
{close && (<Button variant='invert' size='sm' aria-label='Close' data-slot='alert-close' onClick={onClose} className='absolute top-2 right-2 group shrink-0'><X className='size-4 group-hover:opacity-100 opacity-40' /></Button>)}
</div>);
}
export function AlertTitle({ className, ...props }: React.ComponentProps<'div'>) {
return (<div data-slot='alert-title' className={cn('col-start-2 line-clamp-1 min-h-4 font-medium tracking-tight', className)} {...props} />);
}
export function AlertDescription({ className, ...props }: React.ComponentProps<'div'>) {
return (<div data-slot='alert-description' className={cn('text-muted-foreground col-start-2 grid justify-items-start gap-1 text-sm [&_p]:leading-relaxed', className)} {...props} />);
}
export function AlertToolbar({ className, ...props }: React.ComponentProps<'div'>) {
return (<div data-slot='alert-toolbar' className={cn('col-start-2 flex gap-2 items-center mt-1', className)} {...props} />);
}
export function AlertContent({ className, ...props }: React.ComponentProps<'div'>) {
return (<div data-slot='alert-content' className={cn('col-start-2 flex flex-col gap-1', className)} {...props} />);
}

import * as React from 'react';
import { Slot } from '@radix-ui/react-slot';
import { cva, type VariantProps } from 'class-variance-authority';
import { cn } from '@/registry/default/lib/utils';

const buttonVariants = cva(
  'inline-flex items-center justify-center font-medium transition-colors disabled:pointer-events-none disabled:opacity-50 outline-none',
  {
    variants: {
      variant: {
        primary: 'text-primary-foreground bg-primary hover:bg-primary/40',
        secondary: 'text-secondary-foreground bg-secondary hover:bg-secondary/40',
        danger: 'text-destructive-foreground bg-destructive hover:bg-destructive/90',
        invert: 'text-primary bg-transparent'
      },
      mode: {
        icon: 'h-9 w-9 overflow-hidden',
        solid: '',
        outline: 'bg-transparent border-2',
        dashed: 'bg-transparent text-foreground border-foreground border-2 border-dashed',
        ghost: 'bg-transparent hover:bg-accent text-foreground hover:text-foreground/80',
        link: 'bg-transparent text-primary/70 hover:bg-transparent underline underline-offset-4 hover:text-primary'
      },
      size: {
        sm: 'h-7 px-3 text-sm',
        md: 'h-9 px-4 text-base',
        lg: 'h-11 px-6 text-lg'
      },
      shape: {
        rounded: 'rounded-lg',
        pill: 'rounded-full',
        square: 'rounded-none'
      },
      underline: {
        none: '',
        hidden: 'hover:underline underline-offset-4 decoration-2',
        solid: 'underline underline-offset-4 decoration-solid',
        dashed: 'underline underline-offset-4 decoration-dashed decoration-2'
      },
      opacity: {
        full: 'opacity-100',
        medium: 'opacity-70 hover:opacity-80',
        low: 'opacity-50 hover:opacity-60'
      }
    },
    compoundVariants: [
      { mode: 'outline', variant: 'primary', className: 'border-primary text-primary hover:bg-primary/10' },
      { mode: 'outline', variant: 'secondary', className: 'border-secondary text-secondary hover:bg-secondary/10' },
      { mode: 'outline', variant: 'danger', className: 'border-destructive text-destructive hover:bg-destructive/10' },
      { variant: 'danger', underline: 'dashed', className: 'decoration-destructive' }
    ],
    defaultVariants: {
      variant: 'primary',
      mode: 'solid',
      size: 'md',
      shape: 'rounded',
      underline: 'none',
      opacity: 'full'
    }
  }
);

type ButtonProps = React.ComponentProps<'button'> & VariantProps<typeof buttonVariants> & { asChild?: boolean };

function Button({ className, variant, mode, size, shape, underline, opacity, asChild = false, ...props }: ButtonProps) {
  const Comp = asChild ? Slot : 'button';
  return <Comp data-slot='button' className={cn(buttonVariants({ variant, mode, size, shape, underline, opacity, className }))} {...props} />;
}

export { Button, buttonVariants };

import { clsx, type ClassValue } from 'clsx'
;import { twMerge } from 'tailwind-merge';

export function cn(...inputs: ClassValue[]): string {
return twMerge(clsx(inputs));
}
